#ifndef SHADER_PARTICLES_UPDATE_VERT
#define SHADER_PARTICLES_UPDATE_VERT "#version 330 core\n\nlayout (location = 0) in vec3 a_position;\nlayout (location = 1) in float a_rotation;\nlayout (location = 2) in vec2 a_size;\nlayout (location = 3) in vec4 a_color;\nlayout (location = 4) in vec4 a_texRect;\nlayout (location = 5) in vec3 a_velocity;\nlayout (location = 6) in float a_age;\nlayout (location = 7) in int a_type;\n\nout vec3 v_position;\nout float v_rotation;\nout vec2 v_size;\nout vec4 v_color;\nout vec4 v_texRect;\nout vec3 v_velocity;\nout float v_age;\nflat out int v_type;\n\n\n///////////////////////////////////////////////////////////\nvoid main()\n{\n    v_position = a_position;\n    v_rotation = a_rotation;\n    v_size = a_size;\n    v_color = a_color;\n    v_texRect = a_texRect;\n    v_velocity = a_velocity;\n    v_age = a_age;\n    v_type = a_type;\n}"
#endif