#ifndef SHADER_UI_VERT
#define SHADER_UI_VERT "#version 330 core\n\nlayout (location = 0) in vec2 a_position;\nlayout (location = 1) in float a_rotation;\nlayout (location = 2) in vec2 a_size;\nlayout (location = 3) in vec2 a_origin;\nlayout (location = 4) in vec4 a_color;\nlayout (location = 5) in vec4 a_texRect;\nlayout (location = 6) in float a_index;\n\nout vec2 v_position;\nout float v_rotation;\nout vec2 v_size;\nout vec2 v_origin;\nout vec4 v_color;\nout vec2 v_texCoord;\nout vec2 v_texSize;\nout float v_index;\n\n\n///////////////////////////////////////////////////////////\nvoid main()\n{\n    v_position = a_position;\n    v_size = a_size;\n    v_rotation = a_rotation;\n    v_origin = a_origin;\n    v_texCoord = a_texRect.xy;\n    v_texSize = a_texRect.zw;\n    v_color = a_color;\n    v_index = a_index;\n}"
#endif